SELECT CONCAT(employees.first_name,' ', employees.last_name), departments.dept_name, DATEDIFF(dept_emp.to_date, dept_emp.from_date) AS dias_trabalhados FROM dept_emp INNER JOIN employees ON employees.emp_no = dept_emp.emp_no INNER JOIN departments ON departments.dept_no = dept_emp.dept_no ORDER BY dias_trabalhados DESC LIMIT 10;